tissue2T1 = [1 2569 833 500 350 900 569 1 1 500 2569 500]*1e-3;
tissue2T2 = [1 329 83 70 70 47 329 1 1 70 329 70]*1e-3;
tissue2PD = [0 1 0.86 0.77 1 1 1 0 0 0.77 1 0.77];

